                    CKEDITOR.replace('txt_noidung', {
                        toolbar: [['Cut','Copy','Paste','-'],['JustifyLeft','JustifyCenter','JustifyRight','JustifyBlock'],['Bold','Italic','Underline','-','NumberedList','BulletedList'],['Font','FontSize']],
