                <script type="text/javascript">
                    $(document).ready(function(){
                        sendContactUs.loadCaptcha();
                    });

                    var sendContactUs = {
                        checkForm: function() {
                            // reset error
                            $('.name-error, .email-error, .mobile-error, .area-error, .company-error, .business-error, .content-error, .captcha-error').text('');
                            var txtCaptcha = $('#captcha').val();
                            var txtContent     = CKEDITOR.instances['content'].getData();
                            var txtEmail = $('#email').val();
                            var txtName = $('#name').val();
                            var txtMobile = $('#mobile').val();
                            var txtArea = $('#area').val();
                            var txtCompany = $('#company').val();
                            var txtBusiness = $('#business').val();
                            var isValid = true;
                            if (!txtMobile.length) {
                                $('.mobile-error').text('Số điện thoại không được để trống');
                                isValid = false;
                            }
                            if (!txtCompany.length) {
                                $('.company-error').text('Doanh nghiệp không được để trống');
                                isValid = false;
                            }
                            if (!txtBusiness.length) {
                                $('.business-error').text('Loại hình kinh doanh không được để trống');
                                isValid = false;
                            }
                            if (!txtArea.length) {
                                $('.area-error').text('Tỉnh thành không được để trống');
                                isValid = false;
                            }
                            if (txtContent.split(' ').length < 5) {
                                $('.content-error').text('Số lượng chữ tối thiểu là 5 chữ');
                                $('#content').focus();
                                isValid = false;
                            }
                            if (!txtEmail) {
                                $('.email-error').text('Email không được để trống');
                                isValid = false;
                            }
                            if (!txtName) {
                                $('.name-error').text('Họ tên không được để trống');
                                isValid = false;
                            }
                            if (txtCaptcha.length != 4) {
                                $('.captcha-error').text('Mã bảo mật không chính xác.');
                                sendContactUs.loadCaptcha();
                                isValid = false;
                            }
                            if (!this.CheckEmailAddress(txtEmail)) {
                                $('.email-error').text('Địa chỉ email không hợp lệ');
                                isValid = false;
                            }
                            if (isValid) {
                                this.contactUs();
                            }
                            return false;
                        },
                        contactUs: function() {
                            var captchaId   = $('#captchaID2').val();
                            var txtCaptcha = $('#captcha').val();
                            var txtContent     = CKEDITOR.instances['content'].getData();
                            var txtEmail = $('#email').val();
                            var txtName = $('#name').val();
                            var txtMobile = $('#mobile').val();
                            var txtArea = $('#area').val();
                            var txtCompany = $('#company').val();
                            var txtBusiness = $('#business').val();
                            $.ajax({
                                type: "POST",
                                url: base_url + "/contactus/ads",
                                data: {
                                    'name': txtName,
                                    'email': txtEmail,
                                    'content': txtContent,
                                    'mobile': txtMobile,
                                    'area': txtArea,
                                    'company': txtCompany,
                                    'business': txtBusiness,
                                    'captchaId': captchaId,
                                    'captcha': txtCaptcha
                                },
                                dataType: 'json',
                                success: function (result) {
                                    var tmp_alert = '<div id="login-vne4" class="login-form">'
                                        + '<div class="ttOline">Thông báo</div>'
                                        + '<div class="complete-form">'
                                        + '<div class="content_form">{MESSAGE}</div>'
                                        + '</div>'
                                        + '<div class="close-lb" onclick="sendContactUs.clearForm();"></div>'
                                        + '<div class="clear">&nbsp;</div>'
                                        + '</div>';
                                    if (result.error == 2)
                                    {
                                        Sexy.notice(tmp_alert.replace('{MESSAGE}', result.message));
                                        sendContactUs.clearForm();
                                    }
                                    else if(result.error == 3)
                                    {
                                        $('#errorCaptcha').html(result.message);
                                        sendContactUs.loadCaptcha();
                                    }
                                    else
                                    {
                                        Sexy.notice(tmp_alert.replace('{MESSAGE}', 'Gửi liên hệ không thành công'));
                                    }
                                    return false;
                                }
                            });
                        },
                        loadCaptcha: function() {
                            $.ajax({
                                type: 'GET',
                                url: base_url + '/captcha/show2',
                                dataType: 'json',
                                beforeSend: function() {
                                },
                                success: function(response) {
                                    if (response != false) {
                                        $('#showCaptcha').html(response.html);
                                    }
                                }
                            });
                        },
                        goHome: function()
                        {
                            window.location.href = base_url;
                        },
                        clearForm: function()
                        {
                            if(device_env == 4)
                            {
                                CKEDITOR.instances['content'].setData('');
                            }
                            $('#name, #email, #mobile, #area, #company, #business, #content, #captcha').val('');
                            sendContactUs.loadCaptcha();
                            return true;
                        },
                        CheckEmailAddress: function(email) {
                            var re = /^([\w-]+(?:\.[\w-]+)*)@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$/i;
                            return re.test(email);
                        }
                    };
                </script>               </div>
